var jq = $.noConflict();
jq(function(){
	jq(document).ready(function () {
		jq("#search_data").autocomplete({
			delay: 100,
			source: function (request, response) {
				var suggestURL = "https://suggestqueries.google.com/complete/search?client=firefox&cp=2&hl=en&q=%QUERY";
				suggestURL = suggestURL.replace('%QUERY', request.term);

				jq.ajax({
					method: 'GET',
					dataType: 'jsonp',
					jsonpCallback: 'jsonCallback',
					url: suggestURL
				})
				.success(function(data){
					response(data[1]);
				});
			}
		});
	});
});
