function srcFunc(){
	var key = document.getElementById('search_data').value;
	key = key.replace(/[`~!@#$%^&*()_|+\-=?;:'",.<>\{\}\[\]\\\/]/gi, '').replace(/\s\s+/g, ' ');
	var base_url = window.location.origin;
	var url = base_url+'/search/q='+encodeURIComponent(key);
	window.open(url, '_blank');
}
