      var key = document.getElementById('search_data').value;
      key = key.replace(/[`~!@#$%^&*()_|+\-=?;:'",.<>\{\}\[\]\\\/]/gi, '').replace(/\s\s+/g, ' ');
      var base_url = window.location.origin;
      var url = base_url+'/search/q='+encodeURIComponent(key);
      window.open(url, '_blank');
    }
});
(function($) {
  $.fn.agctoc = function(options) {
    var settings = $.extend({
      title:            '',
      headingSelectors: ['h1', 'h2', 'h3', 'h4', 'h5'],
      scrollDuration:   1000
    }, options);

    var toc = [];

    $(settings.headingSelectors.join(',')).each(function() {
      for (var i = 0; i < settings.headingSelectors.length; ++i) {
        if ($(this).is(settings.headingSelectors[i]) === true) {
          if ($(this).attr('id') === undefined) {
            $(this).attr('id', 'agctoc_' + toc.length);
          }
          toc.push({
            title: $(this).html(),
            level: i,
            id:    $(this).attr('id') 
          });
        }
      }
    });

    var tocHTML = '';

    if (settings.title.length > 0) {
      tocHTML += '<div class="agctoc_title">' + settings.title + '</div>'
    }

    for (var i = 0; i < toc.length; ++i) {
      if (i === 0 || toc[i - 1].level < toc[i].level) {
        tocHTML += '<ol class="agctoc_' + toc[i].level + '">';
      }

      tocHTML += '<li>'
              +    '<a href="#' + toc[i].id + '">'
              +      toc[i].title
              +    '</a>'
              +  '</li>';

      if (i === toc.length - 1 || toc[i].level > toc[i + 1].level) {
        tocHTML += '</ol>';
      }
    }

    return this.each(function() {
      $(this).html(tocHTML);

      $(this).find('a').click(function() {
        $('html, body').animate({
            scrollTop: $($(this).attr('href')).offset().top
        }, settings.scrollDuration);
        return false;
      });

      $(this).find('ol').each(function() {
        $(this).css({
          'list-style-type': 'square',
          'list-style-position': 'inside',
          'padding':    '0px',
          'margin':     '0px'
        });
      });

      $(this).find('a').each(function() {
        $(this).css({
          'color': $(this).css('color')
        });
      });
    });
  };
})(jQuery);
